import React, { useEffect, useState } from "react";
import { useSpring, animated } from "react-spring";
import coinImage from '../img/coin.png'; // Replace with the path to your transparent coin image
import PiggyBankImage from '../img/piggybank.png'; // Replace with the path to your piggy bank image

export function ParallaxCoin1() {
  const [scrollY, setScrollY] = useState(0);

  const props = useSpring({
    from: { scrollY: -1000 },
    scrollY,
  });

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      setScrollY(scrollPosition * 0.2);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <animated.div
      className="parallax-container"
      style={{
        transform: props.scrollY.interpolate((s) => `translateY(${s}px)`),
      }}
    >
      <img
        src={coinImage}
        alt="Coin"
        className="parallax-coin"
        height="300"
        width="300"
      />
    </animated.div>
  );
}

export function ParallaxCoin2() {
  const [scrollY, setScrollY] = useState(0);

  const props = useSpring({
    scrollY,
  });

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      setScrollY(scrollPosition * 0.4);
    };
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <animated.div
      className="parallax-container"
      style={{
        transform: props.scrollY.interpolate((s) => `translateY(${s}px)`),
      }}
    >
      <img
        src={coinImage}
        alt="Coin"
        className="parallax-coin"
        height="200"
        width="200"
      />
    </animated.div>
  );
}

export function PiggyBank() {
  return (
    <div className="parallax-container">
      <img
        src={PiggyBankImage}
        alt="Piggy Bank"
        className="parallax-piggy-bank"
        height="300"
        width="300"
      />
    </div>
  );
}
