import React from 'react';
import FeatureButton from './featureButton';

const ImageContainer = ({imgSrc, stylingId, index,  title, bodyContent}) => {

const shouldReverseStyles = index % 2 === 0;
return (
<div className="text-center">
      <div className="container">
        <div id={stylingId}  className="features">
          <div className="col-md-10 col-md-offset-1 section-title">
            <h2>{title}</h2>
          </div>
          <div class={shouldReverseStyles ? "flex-row" : "flex-row-reverse"}>
            <div class="col-lg-6">
              <img src={imgSrc} className="featureImg"/>
            </div>
            <div class="col-lg-6">
              <div class="p-5 mt-4">
                <br/><br/>
                <h1 class="display-4">{title}</h1>
                <p class="lead">{bodyContent} </p>
                <FeatureButton buttonText="Explore Now" /> {/* Pass the button text as a prop */}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
);
};

export default ImageContainer;
