import React, { useEffect, useState } from "react";
import { useSpring, animated } from "react-spring";
import coinImage from '../img/coin.png'; // Replace with the path to your transparent coin image

export function ParallaxCoin1() {
  const [scrollY, setScrollY] = useState(0);

  const props = useSpring({
    from: { scrollY: 0 },
    scrollY,
  });

  useEffect(() => {
    const handleScroll = () => {
      const featuresDiv = document.getElementById('features');
      const scrollPosition = window.scrollY;

      if (scrollPosition >= 0) {
        setScrollY(scrollPosition - 3500); 
      } else {
        setScrollY(0);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <animated.div
      className="parallax-container"
      style={{
        transform: props.scrollY.interpolate((s) => `translateY(${s * 0.2}px)`), // Adjust size
      }}
    >
      <img
        src={coinImage}
        alt="Coin"
        className="parallax-coin"
      />
    </animated.div>
  );
}

export function ParallaxCoin2() {
  const [scrollY, setScrollY] = useState(0);

  const props = useSpring({
    from: { scrollY: 0 },
    scrollY,
  });

  useEffect(() => {
    const handleScroll = () => {
      const featuresDiv = document.getElementById('features');
      const scrollPosition = window.scrollY;

      if (scrollPosition >= 0) {
        setScrollY(scrollPosition - 3000); // Adjust position
      } else {
        setScrollY(0);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <animated.div
      className="parallax-container"
      style={{
        transform: props.scrollY.interpolate((s) => `translateY(${s * 0.3}px)`), 
      }}
    >
      <img
        src={coinImage}
        alt="Coin"
        className="parallax-coin"
      />
    </animated.div>
  );
}
