import React from "react";
import { useSpring, animated } from 'react-spring';
import coinImage from '../img/coin.png'; // Replace with the path to your transparent coin image

function ParallaxCoin() {
  // Define the parallax animation based on the scroll position
  const [{ scrollY }, set] = useSpring(() => ({ scrollY: -2 }));

  // Handle scroll events to update the parallax position
  window.addEventListener('scroll', () => {
    set({ scrollY: window.scrollY });
  });

  return (
    <div className="parallax-container">
      <animated.img
        src={coinImage}
        alt="Coin"
        className="parallax-coin"
        style={{
          transform: `translateY(-50%) ${scrollY.interpolate((s) => `translateY(${s / 2}px)`)}`
        }}
      />
    </div>
  );
}


export const Features = (props) => {
  return (
    <div id="features" className="text-center">
      <div className="container">
        <div className="col-md-10 col-md-offset-1 section-title">
          <h2>Features</h2>
        </div>
        <div className="row">
          {props.data
            ? props.data.map((d, i) => (
                <div key={`${d.title}-${i}`} className="col-xs-6 col-md-3">
                  {" "}
                  <i className={d.icon}></i>
                  <h3>{d.title}</h3>
                  <p>{d.text}</p>
                </div>
              ))
            : "Loading..."}
        </div>
        <h1>Parallax Coin Effect</h1>
        <div className="content">
            <p>Scroll down to see the parallax effect:</p>
            <div className="spacer"></div>
            <ParallaxCoin />
        </div>
      </div>
    </div>
  );
};
