import React, { useEffect, useState } from "react";
import { useSpring, animated } from "react-spring";
import coinImage from '../img/coin.png'; // Replace with the path to your transparent coin image

function ParallaxCoin() {
  const [scrollY, setScrollY] = useState(0);
  const props = useSpring({
    from: { scrollY: 0 },
    scrollY,
  });
  useEffect(() => {
    const handleScroll = () => {
      const featuresDiv = document.getElementById('features');
      const scrollPosition = window.scrollY;

      if (scrollPosition >= 0) {
        setScrollY(scrollPosition - 1500);
      } else {
        setScrollY(0);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <animated.div
      className="parallax-container"
      style={{
        transform: props.scrollY.interpolate((s) => `translateY(${s * 0.9}px)`),
      }}
    >
      <img
        src={coinImage}
        alt="Coin"
        className="parallax-coin" // Apply the CSS class here
      />
    </animated.div>
  );
}


export const Features = (props) => {
  return (
    <div className="text-center">
      <div className="container">
      <div id="features" style={{ height: '100%' }}>
        <div className="col-md-10 col-md-offset-1 section-title">
        <h2>Features</h2>
        </div>
        <div className="row">
          <div className="row">
            {props.data
              ? props.data.map((d, i) => (
                  <div key={`${d.title}-${i}`} className="col-xs-6 col-md-3">
                    {" "}
                    <i className={d.icon}></i>
                    <h3>{d.title}</h3>
                    <p>{d.text}</p>
                  </div>
                ))
              : "Loading..."}
          </div>
        </div>
      </div>
      <ParallaxCoin />
      </div>
    </div>
  );
};
