import React from 'react';
import FeatureButton from './featureButton';

const ImageContainer = ({ imgSrc, stylingId, index, title, bodyContent }) => {
  const shouldReverseStyles = index % 2 === 0;

  return (
    <div className={`text-center ${shouldReverseStyles ? 'reverse-container' : ''}`}>
      <div className="container">
        <div id={stylingId} className="features">
          <div className="col-md-10 col-md-offset-1 section-title">
            <h2>{title}</h2>
          </div>
          <div className={`row ${shouldReverseStyles ? 'reverse-row' : ''}`}>
            <div className="col-lg-6">
              <img src={imgSrc} className="featureImg" />
            </div>
            <div className="col-lg-6">
              <div className="p-5 mt-4">
                <br/><br/>
                <h1 className="display-4">{title}</h1>
                <p className="lead">{bodyContent}</p>
                <FeatureButton buttonText="Explore Now" />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ImageContainer;
