import React, { useEffect } from "react";
import { useSpring, animated } from 'react-spring';
import coinImage from '../img/coin.png'; // Replace with the path to your transparent coin image

function ParallaxCoin() {
  // Define the parallax animation based on the scroll position
  const [{ scrollY }, set] = useSpring(() => ({ scrollY: 0 }));

  // Handle scroll events to update the parallax position
  useEffect(() => {
    const featuresDiv = document.getElementById('features');

    const handleScroll = () => {
      const scrollPosition = featuresDiv.scrollTop;
      set({ scrollY: scrollPosition });
    };

    featuresDiv.addEventListener('scroll', handleScroll);

    return () => {
      featuresDiv.removeEventListener('scroll', handleScroll);
    };
  }, [set]);

  return (
    <animated.div
      className="parallax-container"
      style={{
        transform: scrollY.interpolate((s) => `translateY(${s / 2}px)`),
      }}
    >
      <img
        src={coinImage}
        alt="Coin"
        className="parallax-coin"
      />
    </animated.div>
  );
}

export const Features = (props) => {
  return (
    <div id="features" className="text-center" style={{ overflowY: 'scroll', height: '1000px' }}>
      <div className="container">
        <div className="col-md-10 col-md-offset-1 section-title">
          <h2>Features</h2>
        </div>
        <div className="row">
          {/* Your feature items */}
        </div>
        <h1>Parallax Coin Effect</h1>
        <div className="content">
            <p>Scroll down to see the parallax effect:</p>
            <ParallaxCoin />
        </div>
      </div>
    </div>
  );
};
