import React, { useEffect, useState } from "react";
import { useSpring, animated } from "react-spring";
import coinImage from '../img/coin.png'; // Replace with the path to your transparent coin image

function ParallaxCoin() {
  const [scrollY, setScrollY] = useState(0);

  // Define the parallax animation based on the scroll position within the "features" div
  const props = useSpring({
    from: { scrollY: 0 },
    scrollY,
  });

  // Handle scroll events on the entire page to update the scroll position within the "features" div
  useEffect(() => {
    const handleScroll = () => {
      const featuresDiv = document.getElementById('features');
      const scrollPosition = window.scrollY;

      if (scrollPosition >= 0) {
        setScrollY(scrollPosition - 800);
      } else {
        setScrollY(0);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <animated.div
      className="parallax-container"
      style={{
        transform: props.scrollY.interpolate((s) => `translateY(${s / 2}px)`),
      }}
    >
      <img
        src={coinImage}
        alt="Coin"
        className="parallax-coin" // Apply the CSS class here
      />
    </animated.div>
  );
}


export const Features = (props) => {
  return (
    <div className="text-center">
      <div className="container">
        <div className="col-md-10 col-md-offset-1 section-title">
          <h2>Features</h2>
        </div>
        <div className="row">
          {/* Your feature items */}
        </div>
        <h1>Parallax Coin Effect</h1>
        <div className="content">
          <p>Scroll down to see the parallax effect:</p>
        </div>
      </div>
      <div id="features" style={{ height: '1000px' }}>
        <ParallaxCoin />
      </div>
    </div>
  );
};
