import React, { useEffect } from "react";
import { useSpring, animated } from 'react-spring';
import coinImage from '../img/coin.png'; // Replace with the path to your transparent coin image

function ParallaxCoin() {
  // Define the parallax animation based on the scroll position of the entire page
  const [{ scrollY }, set] = useSpring(() => ({ scrollY: 0 }));

  // Handle scroll events to update the parallax position
  useEffect(() => {
    const handleScroll = () => {
      const scrolled = window.scrollY;

      // Adjust the range based on where you want the animation to take place
      if (scrolled >= 100 && scrolled <= 200) {
        set({ scrollY: scrolled - 100 });
      } else if (scrolled < 100) {
        set({ scrollY: 0 });
      } else if (scrolled > 200) {
        set({ scrollY: 100 });
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [set]);

  return (
    <animated.div
      className="parallax-container"
      style={{
        transform: scrollY.interpolate((s) => `translateY(${s}px)`),
      }}
    >
      <img
        src={coinImage}
        alt="Coin"
        className="parallax-coin"
        style={{
          width: '50px', // Adjust the width as needed
          height: '50px', // Adjust the height as needed
        }}
      />
    </animated.div>
  );
}

export const Features = (props) => {
  return (
    <div className="text-center">
      <div className="container">
        <div className="col-md-10 col-md-offset-1 section-title">
          <h2>Features</h2>
        </div>
        <div className="row">
          {/* Your feature items */}
        </div>
        <h1>Parallax Coin Effect</h1>
        <div className="content">
          <p>Scroll down to see the parallax effect:</p>
        </div>
      </div>
      <ParallaxCoin />
    </div>
  );
};
